mapper.update:
UPDATE workers
SET date_of_dismissal = #{dateOfDismissal}
WHERE firstname = #{firstName} AND lastname = #{lastName};

UPDATE workers
SET department_id = (SELECT id FROM departments WHERE name = #{nameDepartment})
WHERE firstname = #{firstName} AND lastname = #{lastName};

UPDATE workers SET department_id = (SELECT id FROM departments WHERE name = #{newDepartment}),
head_department = false
WHERE department_id = (SELECT id FROM departments WHERE name = #{oldDepartment});

mapper.select:
SELECT * FROM workers
WHERE department_id = (SELECT department_id FROM workers WHERE firstname = #{firstName} AND lastname = #{lastName})
AND head_department = true;

mapper.update:
UPDATE workers SET head_department = false
WHERE department_id = (SELECT department_id FROM workers WHERE  firstname = #{firstName} AND lastname = #{lastName});

UPDATE workers SET head_department = true
WHERE firstname = #{firstName} AND lastname = #{lastName};
